import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styles from "./navMobile.module.css";

export default function NavMobile({ isStatic = false }) {
const [isOpen, setIsOpen] = useState<boolean>(false);
	const [scrollY, setScrollY] = useState<number>(0);
	const open = () => {
		setScrollY(window.scrollY);
		setIsOpen(true);
	};
	const close = () => {
		setIsOpen(false);
	};

	useEffect(() => {
		if (isOpen) {
			document.body.style.position = "fixed";
			document.body.style.top = `-${scrollY}px`;
		} else {
			document.body.style.position = "";
			document.body.style.top = "";
			window.scrollTo(0, scrollY);
		}
	}, [isOpen, scrollY]);

	return (
		<>
			<nav
				className={`
        ${styles.navWrapper} 
        ${isStatic && styles.staticNav}
        ${isOpen && styles.open}
      `}
			>
				<div
					className={`${styles.navMobileOpenContainer} ${
						isOpen && styles.open
					}`}
				>
					<div className={styles.navMobileContainer}>
						<Link href="/">
							<Logo className={styles.logo} />
						</Link>
						<ul className={styles.navMobileLinks}>
							<li onClick={!isOpen ? open : close}>
								<Burger />
							</li>
						</ul>
					</div>
					<ul className={styles.navMobileOpenLinks}>
						<h1>Menu</h1>
						<Link href="/" onClick={!isOpen ? open : close}>
							<li>HOME</li>
						</Link>

						<Link href="/merch" onClick={!isOpen ? open : close}>
							<li>MERCH</li>
						</Link>

						<Link href="/signup" onClick={!isOpen ? open : close}>
							<li>SIGNUP</li>
						</Link>

                        <Link href="/contact" onClick={!isOpen ? open : close}>
							<li>CONTACT</li>
						</Link>
					</ul>
				</div>

				<div
					className={`
          ${styles.navMobileContainer}
          ${isStatic && styles.navTopBorder}
        `}
				>
					<Link href="/">
						<Logo className={styles.logo} />
					</Link>
					<ul className={styles.navMobileLinks}>
						{!isOpen}
						<li className={styles.burger} onClick={!isOpen ? open : close}>
							<Burger />
						</li>
					</ul>
				</div>
			</nav>
		</>
	);
}
