import "./navMobile.css";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Logo from "./images/DuckyLogo.png";
import Burger from "./icons/burger.tsx";

export default function NavMobile({ isStatic = false }) {
  const [isOpen, setIsOpen] = useState(false);
  const [scrollY, setScrollY] = useState(0);
  const open = () => {
    setScrollY(window.scrollY);
    setIsOpen(true);
  };
  const close = () => {
    setIsOpen(false);
  };

  useEffect(() => {
    if (isOpen) {
      document.body.style.position = "fixed";
      document.body.style.top = `-${scrollY}px`;
    } else {
      document.body.style.position = "";
      document.body.style.top = "";
      window.scrollTo(0, scrollY);
    }
  }, [isOpen, scrollY]);

  return (
    <>
      <div>
        <Link href="/">
          <img className="logo" src={Logo} alt="Ducky Logo" />
        </Link>
        <ul className="navMobileLinks">
          {!isOpen}
          <li className="burger" onClick={!isOpen ? open : close}>
            <Burger />
          </li>
        </ul>
      </div>
      <nav>
        {isOpen && (
          <div>
            <div className="navMobileContainer">
              <Link href="/">
                <img className="logo" src={Logo} alt="Ducky Logo" />
              </Link>
              <ul className="navMobileLinks">
                <li onClick={!isOpen ? open : close}>
                  <Burger />
                </li>
              </ul>
            </div>
            <ul className="navMobileOpenLinks">
              <h1>Menu</h1>
              <Link href="/" onClick={!isOpen ? open : close}>
                <li>HOME</li>
              </Link>

              <Link href="/merch" onClick={!isOpen ? open : close}>
                <li>MERCH</li>
              </Link>

              <Link href="/signup" onClick={!isOpen ? open : close}>
                <li>SIGNUP</li>
              </Link>

              <Link href="/contact" onClick={!isOpen ? open : close}>
                <li>CONTACT</li>
              </Link>
            </ul>
          </div>
        )}
      </nav>
    </>
  );
}
